import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Auth.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showUserModal, setShowUserModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
    const darkMode = localStorage.getItem('darkMode') === 'true';
    setIsDarkMode(darkMode);
    if (darkMode) {
      document.body.classList.add('dark-mode');
    }
  }, []);

  useEffect(() => {
    filterUsers();
  }, [searchTerm, roleFilter, statusFilter, users]);

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/admin/users', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(res.data.users);
      setFilteredUsers(res.data.users);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch users');
      setLoading(false);
    }
  };

  const filterUsers = () => {
    let filtered = users;

    if (searchTerm) {
      filtered = filtered.filter(user =>
        user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (roleFilter !== 'all') {
      filtered = filtered.filter(user => user.role === roleFilter);
    }

    if (statusFilter !== 'all') {
      const isBlocked = statusFilter === 'blocked';
      filtered = filtered.filter(user => user.isBlocked === isBlocked);
    }

    setFilteredUsers(filtered);
  };

  const handleBlock = async (userId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/admin/block/${userId}`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchUsers();
    } catch (err) {
      alert('Failed to block/unblock user');
    }
  };

  const handlePromote = async (userId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/admin/promote/${userId}`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchUsers();
    } catch (err) {
      alert('Failed to promote user');
    }
  };

  const handleDelete = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user? This action cannot be undone.')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/api/admin/delete/${userId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        fetchUsers();
        setShowUserModal(false);
      } catch (err) {
        alert('Failed to delete user');
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const toggleDarkMode = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('darkMode', newMode);
    document.body.classList.toggle('dark-mode');
  };

  const openUserModal = (user) => {
    setSelectedUser(user);
    setShowUserModal(true);
  };

  if (loading) return (
    <div className="loading-screen">
      <div className="loading-spinner"></div>
      <p>Loading dashboard...</p>
    </div>
  );

  const totalUsers = users.length;
  const adminCount = users.filter(u => u.role === 'admin').length;
  const blockedCount = users.filter(u => u.isBlocked).length;
  const activeCount = totalUsers - blockedCount;

  return (
    <div className={`dashboard-modern admin-dashboard ${isDarkMode ? 'dark' : ''}`}>
      {/* Top Navigation */}
      <nav className="dashboard-nav">
        <div className="nav-left">
          <div className="brand-logo-small">
            <span className="logo-icon">ğŸš€</span>
            <span className="brand-name">Admin Panel</span>
          </div>
        </div>

        <div className="nav-right">
          <button className="icon-btn" onClick={toggleDarkMode}>
            {isDarkMode ? 'â˜€ï¸' : 'ğŸŒ™'}
          </button>
          <button onClick={handleLogout} className="btn-logout-nav">
            Logout
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <div className="dashboard-content-modern">
        {/* Header */}
        <div className="admin-header">
          <div>
            <h1>User Management</h1>
            <p>Manage and monitor all registered users</p>
          </div>
        </div>

        {error && <div className="error-message-modern">{error}</div>}

        {/* Stats Overview */}
        <div className="stats-grid-modern">
          <div className="stat-card-modern">
            <div className="stat-icon" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
              ğŸ‘¥
            </div>
            <div className="stat-info">
              <h3>{totalUsers}</h3>
              <p>Total Users</p>
            </div>
          </div>

          <div className="stat-card-modern">
            <div className="stat-icon" style={{ background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)' }}>
              ğŸ‘‘
            </div>
            <div className="stat-info">
              <h3>{adminCount}</h3>
              <p>Administrators</p>
            </div>
          </div>

          <div className="stat-card-modern">
            <div className="stat-icon" style={{ background: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)' }}>
              âœ…
            </div>
            <div className="stat-info">
              <h3>{activeCount}</h3>
              <p>Active Users</p>
            </div>
          </div>

          <div className="stat-card-modern">
            <div className="stat-icon" style={{ background: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)' }}>
              ğŸš«
            </div>
            <div className="stat-info">
              <h3>{blockedCount}</h3>
              <p>Blocked Users</p>
            </div>
          </div>
        </div>

        {/* Filters and Search */}
        <div className="users-control-panel">
          <div className="search-filter-section">
            <div className="search-box-modern">
              <span className="search-icon">ğŸ”</span>
              <input
                type="text"
                placeholder="Search by name or email..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <div className="filter-group">
              <select 
                value={roleFilter} 
                onChange={(e) => setRoleFilter(e.target.value)}
                className="filter-select"
              >
                <option value="all">All Roles</option>
                <option value="user">Users</option>
                <option value="admin">Admins</option>
              </select>

              <select 
                value={statusFilter} 
                onChange={(e) => setStatusFilter(e.target.value)}
                className="filter-select"
              >
                <option value="all">All Status</option>
                <option value="active">Active</option>
                <option value="blocked">Blocked</option>
              </select>
            </div>
          </div>

          <div className="results-info">
            Showing {filteredUsers.length} of {totalUsers} users
          </div>
        </div>

        {/* Users Table */}
        <div className="users-table-modern">
          {filteredUsers.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>User</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Status</th>
                  <th>Joined</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredUsers.map(user => (
                  <tr key={user._id}>
                    <td>
                      <div className="user-cell">
                        <img 
                          src={`https://ui-avatars.com/api/?name=${user.name}&background=random&size=40`}
                          alt={user.name}
                          className="user-avatar-small"
                        />
                        <span className="user-name">{user.name}</span>
                      </div>
                    </td>
                    <td>{user.email}</td>
                    <td>
                      <span className={`badge-modern ${user.role}`}>
                        {user.role === 'admin' ? 'ğŸ‘‘' : 'ğŸ‘¤'} {user.role}
                      </span>
                    </td>
                    <td>
                      <span className={`badge-modern ${user.isBlocked ? 'blocked' : 'active'}`}>
                        {user.isBlocked ? 'ğŸš« Blocked' : 'âœ… Active'}
                      </span>
                    </td>
                    <td>{new Date(user.createdAt).toLocaleDateString()}</td>
                    <td>
                      <div className="action-buttons">
                        <button 
                          onClick={() => openUserModal(user)}
                          className="action-btn view"
                          title="View Details"
                        >
                          ğŸ‘ï¸
                        </button>
                        <button 
                          onClick={() => handleBlock(user._id)}
                          className={`action-btn ${user.isBlocked ? 'unblock' : 'block'}`}
                          title={user.isBlocked ? 'Unblock' : 'Block'}
                        >
                          {user.isBlocked ? 'âœ…' : 'ğŸš«'}
                        </button>
                        {user.role !== 'admin' && (
                          <button 
                            onClick={() => handlePromote(user._id)}
                            className="action-btn promote"
                            title="Promote to Admin"
                          >
                            â¬†ï¸
                          </button>
                        )}
                        <button 
                          onClick={() => handleDelete(user._id)}
                          className="action-btn delete"
                          title="Delete User"
                        >
                          ğŸ—‘ï¸
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <div className="no-results">
              <span className="no-results-icon">ğŸ”</span>
              <h3>No users found</h3>
              <p>Try adjusting your search or filters</p>
            </div>
          )}
        </div>
      </div>

      {/* User Detail Modal */}
      {showUserModal && selectedUser && (
        <div className="modal-overlay" onClick={() => setShowUserModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close" onClick={() => setShowUserModal(false)}>âœ•</button>
            
            <div className="modal-header">
              <img 
                src={`https://ui-avatars.com/api/?name=${selectedUser.name}&background=random&size=80`}
                alt={selectedUser.name}
                className="modal-avatar"
              />
              <h2>{selectedUser.name}</h2>
              <p>{selectedUser.email}</p>
            </div>

            <div className="modal-body">
              <div className="modal-info-grid">
                <div className="modal-info-item">
                  <span className="info-label">Role</span>
                  <span className={`badge-modern ${selectedUser.role}`}>
                    {selectedUser.role}
                  </span>
                </div>
                <div className="modal-info-item">
                  <span className="info-label">Status</span>
                  <span className={`badge-modern ${selectedUser.isBlocked ? 'blocked' : 'active'}`}>
                    {selectedUser.isBlocked ? 'Blocked' : 'Active'}
                  </span>
                </div>
                <div className="modal-info-item">
                  <span className="info-label">Joined</span>
                  <span>{new Date(selectedUser.createdAt).toLocaleString()}</span>
                </div>
                <div className="modal-info-item">
                  <span className="info-label">Last Updated</span>
                  <span>{new Date(selectedUser.updatedAt).toLocaleString()}</span>
                </div>
              </div>
            </div>

            <div className="modal-footer">
              <button 
                onClick={() => handleBlock(selectedUser._id)}
                className={`btn-modern ${selectedUser.isBlocked ? 'btn-success' : 'btn-warning'}`}
              >
                {selectedUser.isBlocked ? 'Unblock User' : 'Block User'}
              </button>
              {selectedUser.role !== 'admin' && (
                <button 
                  onClick={() => handlePromote(selectedUser._id)}
                  className="btn-modern btn-primary"
                >
                  Promote to Admin
                </button>
              )}
              <button 
                onClick={() => handleDelete(selectedUser._id)}
                className="btn-modern btn-danger"
              >
                Delete User
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
