import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './Auth.css';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const { email, password } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError('');
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', formData);
      
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      
      if (res.data.user.role === 'admin') {
        navigate('/admin-dashboard');
      } else {
        navigate('/dashboard');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container-modern">
      <div className="auth-background">
        <div className="gradient-circle circle-1"></div>
        <div className="gradient-circle circle-2"></div>
        <div className="gradient-circle circle-3"></div>
      </div>

      <div className="auth-card-modern">
        <div className="auth-header-modern">
          <div className="brand-logo">
            <div className="logo-icon">üöÄ</div>
            <h1>Authra</h1>
          </div>
          <h2>Welcome Back</h2>
          <p>Enter your credentials to access your account</p>
        </div>
        
        {error && (
          <div className="error-message-modern">
            <span className="error-icon">‚ö†Ô∏è</span>
            {error}
          </div>
        )}
        
        <form onSubmit={onSubmit} className="auth-form-modern">
          <div className="form-group-modern">
            <label>
              <span className="label-icon">üìß</span>
              Email Address
            </label>
            <div className="input-wrapper">
              <input
                type="email"
                name="email"
                value={email}
                onChange={onChange}
                required
                placeholder="you@example.com"
                disabled={loading}
                className="input-modern"
              />
            </div>
          </div>

          <div className="form-group-modern">
            <label>
              <span className="label-icon">üîí</span>
              Password
            </label>
            <div className="input-wrapper password-wrapper">
              <input
                type={showPassword ? 'text' : 'password'}
                name="password"
                value={password}
                onChange={onChange}
                required
                placeholder="Enter your password"
                disabled={loading}
                className="input-modern"
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
              </button>
            </div>
          </div>

          <div className="form-footer-modern">
            <label className="remember-me">
              <input type="checkbox" />
              <span>Remember me</span>
            </label>
            <Link to="/forgot-password" className="forgot-link">
              Forgot password?
            </Link>
          </div>

          <button type="submit" disabled={loading} className="btn-primary-modern">
            {loading ? (
              <>
                <span className="spinner"></span>
                Signing in...
              </>
            ) : (
              <>
                <span>Sign In</span>
                <span className="btn-arrow">‚Üí</span>
              </>
            )}
          </button>
        </form>

        <div className="auth-divider">
          <span>OR</span>
        </div>

        <div className="social-login">
          <button className="social-btn google">
            <img src="https://www.google.com/favicon.ico" alt="Google" />
            Continue with Google
          </button>
        </div>

        <div className="auth-footer-modern">
          <p>
            Don't have an account? 
            <Link to="/signup" className="signup-link"> Create one</Link>
          </p>
        </div>
      </div>

      <div className="auth-features">
        <div className="feature-item">
          <span className="feature-icon">üîê</span>
          <div>
            <h4>Secure & Encrypted</h4>
            <p>Your data is protected with industry-standard encryption</p>
          </div>
        </div>
        <div className="feature-item">
          <span className="feature-icon">‚ö°</span>
          <div>
            <h4>Fast & Reliable</h4>
            <p>Lightning-fast performance with 99.9% uptime</p>
          </div>
        </div>
        <div className="feature-item">
          <span className="feature-icon">üéØ</span>
          <div>
            <h4>Easy to Use</h4>
            <p>Intuitive interface designed for everyone</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
