import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import {
  Search, Bell, Home, Activity, Settings, Shield, HelpCircle,
  Clock, CheckCircle, Star, ChevronRight, User, Lock, Mail, BarChart3
} from 'lucide-react';
import './Auth.css';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState([]);
  const [activities, setActivities] = useState([]);
  const [notifications, setNotifications] = useState([
    { id: 1, title: 'Welcome!', message: 'Your account has been created successfully', time: '2 min ago', unread: true },
    { id: 2, title: 'Profile Update', message: 'Complete your profile to unlock all features', time: '1 hour ago', unread: true },
    { id: 3, title: 'Security Alert', message: 'New login from Windows PC', time: '2 hours ago', unread: false }
  ]);
  const [showNotifications, setShowNotifications] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);
  const navigate = useNavigate();

 useEffect(() => {
  const token = localStorage.getItem("token");
  if (!token) {
    navigate("/login");
    return;
  }

  fetch("http://localhost:5000/api/user/me", {
    headers: { Authorization: `Bearer ${token}` }
  })
    .then(res => {
      if (!res.ok) throw new Error("Unauthorized");
      return res.json();
    })
    .then(data => setUser(data))
    .catch(() => {
      localStorage.clear();
      navigate("/login");
    });
}, [navigate]);


  // Fetch stats
  useEffect(() => {
    const token = localStorage.getItem("token");
fetch("http://localhost:5000/api/dashboard/stats", {
  headers: { Authorization: `Bearer ${token}` }
})

      .then(res => res.json())
      .then(data => {
        setStats([
          { icon: <BarChart3 />, value: data.totalActivities, label: 'Total Activities', change: '+12%', color: '#6366f1' },
          { icon: <Clock />, value: data.activeTime, label: 'Active Time', change: '+5%', color: '#8b5cf6' },
          { icon: <CheckCircle />, value: data.completed, label: 'Completed Tasks', change: '+8%', color: '#06b6d4' },
          { icon: <Star />, value: data.successRate, label: 'Success Rate', change: '+2%', color: '#10b981' }
        ]);
      });
  }, []);

  // Fetch activity
  useEffect(() => {
    const token = localStorage.getItem("token");
fetch("http://localhost:5000/api/dashboard/activity", {
  headers: { Authorization: `Bearer ${token}` }
})

      .then(res => res.json())
      .then(data => setActivities(data));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const markAsRead = (id) => {
    setNotifications(prev =>
      prev.map(n => n.id === id ? { ...n, unread: false } : n)
    );
  };

  const unreadCount = notifications.filter(n => n.unread).length;

  if (!user) {
    return (
      <div className="loading-screen">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      {/* NAVBAR */}
      <nav className="navbar">
        <div className="nav-left">
          <div className="brand-logo-sm">
            <Shield size={24} />
            <span>Authra</span>
          </div>
        </div>

        <div className="nav-center">
          <div className="search-box">
            <Search size={18} />
            <input type="text" placeholder="Search..." />
          </div>
        </div>

        <div className="nav-right">
          <button className="icon-btn"><Home size={20} /></button>

          {/* Notifications */}
          <div className="notification-wrapper">
            <button className="icon-btn" onClick={() => setShowNotifications(!showNotifications)}>
              <Bell size={20} />
              {unreadCount > 0 && <span className="badge">{unreadCount}</span>}
            </button>

            {showNotifications && (
              <div className="dropdown notification-dropdown">
                <div className="dropdown-header">
                  <h3>Notifications</h3>
                  <button onClick={() => setNotifications([])}>Clear all</button>
                </div>
                <div className="notification-list">
                  {notifications.length > 0 ? (
                    notifications.map(n => (
                      <div key={n.id} className={`notification-item ${n.unread ? 'unread' : ''}`}
                        onClick={() => markAsRead(n.id)}>
                        <div className="notification-content">
                          <h4>{n.title}</h4>
                          <p>{n.message}</p>
                          <span className="time">{n.time}</span>
                        </div>
                        {n.unread && <div className="unread-dot"></div>}
                      </div>
                    ))
                  ) : (
                    <div className="no-notifications">
                      <span style={{ fontSize: '48px' }}>ðŸ“­</span>
                      <p>No notifications</p>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>

          {/* USER MENU */}
          <div className="user-menu-wrapper">
            <div className="user-avatar" onClick={() => setShowUserMenu(!showUserMenu)}>
              {user?.name?.charAt(0) || 'A'}
            </div>

            {showUserMenu && (
              <div className="user-dropdown">
                <div className="user-dropdown-header">
                  <div className="avatar-sm">{user?.name?.charAt(0)}</div>
                  <div>
                    <strong>{user.name}</strong>
                    <p>{user.email}</p>
                  </div>
                </div>

                <div className="user-dropdown-body">
                  <div className="action-card settings-card" onClick={() => navigate('/settings')}>
                    <h4>Settings</h4>
                    <p>Manage your account preferences</p>
                  </div>
                  <div className="action-card logout-card" onClick={handleLogout}>
                    <h4>Logout</h4>
                    <p>Sign out from your account</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>

      {/* CONTENT */}
      <div className="dashboard-content">
        {/* Welcome */}
        <div className="welcome-banner">
          <div className="banner-text">
            <h1>Welcome back, {user?.name?.split(' ')[0] || 'User'}!</h1>
            <p>Here's what's happening with your account today</p>
          </div>
        </div>

        {/* Stats */}
        <div className="stats-grid">
          {stats.map((s, i) => (
            <div key={i} className="stat-card">
              <div className="stat-icon" style={{ backgroundColor: s.color }}>{s.icon}</div>
              <div className="stat-info">
                <h3>{s.value}</h3>
                <p>{s.label}</p>
                <span className="change positive">{s.change} this week</span>
              </div>
            </div>
          ))}
        </div>

        {/* Activity */}
        <div className="activity-section">
          <div className="section-header">
            <h2>Recent Activity</h2>
          </div>
          <div className="activity-list">
            {activities.map(a => (
              <div key={a.id} className="activity-item">
                <div className="activity-content">
                  <h4>{a.title}</h4>
                  <p>{a.desc}</p>
                  <span className="time">{a.time}</span>
                </div>
              </div>
            ))}
          </div>
        </div>

      </div>
    </div>
  );
};

export default Dashboard;
