import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { Search, Bell, Home, Shield, Clock, CheckCircle, Star, BarChart3 } from "lucide-react";
import "./Auth.css";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState([]);
  const [activities, setActivities] = useState([]);
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [showNotifications, setShowNotifications] = useState(false);
  const navigate = useNavigate();
  const notifRef = useRef(null);

  const sidebarItems = [
    "File Manager",
    "Activity Logs",
    "Security Center",
    "Analytics",
    "Messages",
    "Team Members"
  ];

  // Close notification on outside click
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (notifRef.current && !notifRef.current.contains(e.target)) {
        setShowNotifications(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Auth
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }
    fetch("http://localhost:5000/api/user/me", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setUser(data))
      .catch(() => navigate("/login"));
  }, [navigate]);

  // Stats
  useEffect(() => {
    fetch("http://localhost:5000/api/dashboard/activity", {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
    })
      .then(res => res.json())
      .then(data => {
        setStats([
          { icon: <BarChart3 />, value: data.totalActivities, label: "Total Activities", change: "+12%", color: "#6366f1" },
          { icon: <Clock />, value: data.activeTime, label: "Active Time", change: "+5%", color: "#8b5cf6" },
          { icon: <CheckCircle />, value: data.completed, label: "Completed Tasks", change: "+8%", color: "#06b6d4" },
          { icon: <Star />, value: data.successRate, label: "Success Rate", change: "+2%", color: "#10b981" }
        ]);
        setActivities(data.activities || []);
      });
  }, []);

  if (!user) return <div className="loading-screen">Loading...</div>;

  return (
    <div className="dashboard">
      {/* NAVBAR */}
      <nav className="navbar">
        <div className="nav-left">
          <div className="brand-logo-sm">
            <Shield size={22} />
            <span>Authra</span>
          </div>
        </div>

        <div className="nav-center">
          <div className="search-box">
            <Search size={18} />
            <input placeholder="Search..." />
          </div>
        </div>

        <div className="nav-right">
          <button
  className="icon-btn"
  onClick={() => setActiveMenu("Dashboard")}
  title="Go to Dashboard"
>
  <Home size={18} />
</button>

          <div className="notification-wrapper" ref={notifRef}>
            <button className="icon-btn" onClick={() => setShowNotifications(!showNotifications)}>
              <Bell size={18} />
            </button>
            {showNotifications && (
              <div className="dropdown notification-dropdown">
                <h3 style={{ padding: 20 }}>No new notifications</h3>
              </div>
            )}
          </div>
          <div className="user-avatar">{user.name[0]}</div>
        </div>
      </nav>

      <div className="dashboard-layout">
        {/* SIDEBAR */}
        <div className="sidebar">
          {sidebarItems.map(item => (
            <div
              key={item}
              className={`sidebar-item ${activeMenu === item ? "active" : ""}`}
              onClick={() => setActiveMenu(item)}
            >
              {item}
            </div>
          ))}
        </div>

        {/* MAIN CONTENT */}
        <div className="dashboard-main">
          {activeMenu !== "Dashboard" ? (
            <div className="coming-soon-card">
              <h1>{activeMenu}</h1>
              <p>ðŸš§ Coming Soon...</p>
            </div>
          ) : (
            <>
              <div className="welcome-banner">
                <h1>Welcome back, {user.name.split(" ")[0]}!</h1>
                <p>Here's what's happening with your account today</p>
              </div>

              <div className="stats-grid">
                {stats.map((s, i) => (
                  <div key={i} className="stat-card">
                    <div className="stat-icon" style={{ background: s.color }}>{s.icon}</div>
                    <div>
                      <h3>{s.value}</h3>
                      <p>{s.label}</p>
                      <span className="change positive">{s.change}</span>
                    </div>
                  </div>
                ))}
              </div>

              <div className="activity-section">
                <h2>Recent Activity</h2>
                {activities.length === 0 && <p>No recent activity</p>}
                {activities.map((a, i) => (
                  <div key={i} className="activity-item">
                    <h4>{a.title}</h4>
                    <p>{a.desc}</p>
                    <span>{a.time}</span>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
