import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>User Dashboard</h1>
        <button onClick={handleLogout} className="btn-logout">
          Logout
        </button>
      </div>

      <div className="dashboard-content">
        <div className="welcome-card">
          <h2>Welcome, {user.name}! üëã</h2>
          <p className="user-email">{user.email}</p>
          <div className="user-info">
            <div className="info-item">
              <span className="label">Role:</span>
              <span className="value badge">{user.role}</span>
            </div>
            <div className="info-item">
              <span className="label">Account Status:</span>
              <span className={`value badge ${user.isBlocked ? 'blocked' : 'active'}`}>
                {user.isBlocked ? 'Blocked' : 'Active'}
              </span>
            </div>
          </div>
        </div>

        <div className="feature-cards">
          <div className="feature-card">
            <h3>üìä Your Activity</h3>
            <p>View your recent activities and usage statistics</p>
          </div>
          <div className="feature-card">
            <h3>‚öôÔ∏è Settings</h3>
            <p>Manage your account settings and preferences</p>
          </div>
          <div className="feature-card">
            <h3>üîî Notifications</h3>
            <p>Stay updated with important alerts</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
