import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import {
  Search, Bell, Home, Activity, Settings, Shield, HelpCircle,
  Clock, CheckCircle, Star, ChevronRight, User, Lock, Mail, BarChart3
} from 'lucide-react';
import './Auth.css';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [notifications, setNotifications] = useState([
    { id: 1, title: 'Welcome!', message: 'Your account has been created successfully', time: '2 min ago', unread: true },
    { id: 2, title: 'Profile Update', message: 'Complete your profile to unlock all features', time: '1 hour ago', unread: true },
    { id: 3, title: 'Security Alert', message: 'New login from Windows PC', time: '2 hours ago', unread: false }
  ]);
  const [showNotifications, setShowNotifications] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (!userData) {
      navigate('/login');
      return;
    }
    setUser(JSON.parse(userData));
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const markAsRead = (id) => {
    setNotifications(prev =>
      prev.map(n => n.id === id ? { ...n, unread: false } : n)
    );
  };

  const unreadCount = notifications.filter(n => n.unread).length;

  const stats = [
    { icon: <BarChart3 />, value: '28', label: 'Total Activities', change: '+12%', color: '#6366f1' },
    { icon: <Clock />, value: '42h', label: 'Active Time', change: '+5%', color: '#8b5cf6' },
    { icon: <CheckCircle />, value: '15', label: 'Completed Tasks', change: '+8%', color: '#06b6d4' },
    { icon: <Star />, value: '98%', label: 'Success Rate', change: '+2%', color: '#10b981' }
  ];

  const features = [
    { icon: <Activity />, title: 'Your Activity', description: 'Track progress and analytics', link: '/activity' },
    { icon: <Settings />, title: 'Settings', description: 'Customize preferences', link: '/settings' },
    { icon: <Shield />, title: 'Security', description: 'Manage security settings', link: '/security' },
    { icon: <HelpCircle />, title: 'Help & Support', description: 'Get help and support', link: '/help' }
  ];

  if (!user) {
    return (
      <div className="loading-screen">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <nav className="navbar">
        <div className="nav-left">
          <div className="brand-logo-sm">
            <Shield size={24} />
            <span>Authra</span>
          </div>
        </div>

        <div className="nav-center">
          <div className="search-box">
            <Search size={18} />
            <input type="text" placeholder="Search..." />
          </div>
        </div>

        <div className="nav-right">
          <button className="icon-btn">
            <Home size={20} />
          </button>

          {/* Notifications */}
          <div className="notification-wrapper">
            <button className="icon-btn" onClick={() => setShowNotifications(!showNotifications)}>
              <Bell size={20} />
              {unreadCount > 0 && <span className="badge">{unreadCount}</span>}
            </button>

            {showNotifications && (
              <div className="dropdown notification-dropdown">
                <div className="dropdown-header">
                  <h3>Notifications</h3>
                  <button onClick={() => setNotifications([])}>Clear all</button>
                </div>
                <div className="notification-list">
                  {notifications.length > 0 ? (
                    notifications.map(n => (
                      <div
                        key={n.id}
                        className={`notification-item ${n.unread ? 'unread' : ''}`}
                        onClick={() => markAsRead(n.id)}
                      >
                        <div className="notification-content">
                          <h4>{n.title}</h4>
                          <p>{n.message}</p>
                          <span className="time">{n.time}</span>
                        </div>
                        {n.unread && <div className="unread-dot"></div>}
                      </div>
                    ))
                  ) : (
                    <div className="no-notifications">
                      <span style={{ fontSize: '48px' }}>ðŸ“­</span>
                      <p>No notifications</p>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>

          {/* USER MENU */}
          <div className="user-menu-wrapper">
            <div className="user-avatar" onClick={() => setShowUserMenu(!showUserMenu)}>
              {user?.name?.charAt(0) || 'A'}
            </div>

            {showUserMenu && (
              <div className="user-dropdown">
                <div className="user-dropdown-header">
                  <div className="avatar-sm">{user?.name?.charAt(0)}</div>
                  <div>
                    <strong>{user.name}</strong>
                    <p>{user.email}</p>
                  </div>
                </div>

                <div className="user-dropdown-body">
                  <div
                    className="action-card settings-card"
                    onClick={() => {
                      setShowUserMenu(false);
                      navigate('/settings');
                    }}
                  >
                    <h4>Settings</h4>
                    <p>Manage your account preferences</p>
                  </div>

                  <div
                    className="action-card logout-card"
                    onClick={handleLogout}
                  >
                    <h4>Logout</h4>
                    <p>Sign out from your account</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>

      <div className="dashboard-content">
        <div className="welcome-banner">
          <div className="banner-text">
            <h1>Welcome back, {user?.name?.split(' ')[0] || 'User'}!</h1>
            <p>Here's what's happening with your account today</p>
          </div>

          <div className="user-info">
            <div className="info-item">
              <span className="label">Email</span>
              <span className="value">{user.email}</span>
            </div>
            <div className="info-item">
              <span className="label">Role</span>
              <span className={`badge badge-${user.role}`}>{user.role}</span>
            </div>
            <div className="info-item">
              <span className="label">Status</span>
              <span className={`badge badge-${user.isBlocked ? 'blocked' : 'active'}`}>
                {user.isBlocked ? 'Blocked' : 'Active'}
              </span>
            </div>
          </div>
        </div>

        {/* Stats */}
        <div className="stats-grid">
          {stats.map((s, i) => (
            <div key={i} className="stat-card">
              <div className="stat-icon" style={{ backgroundColor: s.color }}>{s.icon}</div>
              <div className="stat-info">
                <h3>{s.value}</h3>
                <p>{s.label}</p>
                <span className="change positive">{s.change} this week</span>
              </div>
            </div>
          ))}
        </div>

        {/* Features */}
        <div className="features-grid">
          {features.map((f, i) => (
            <Link key={i} to={f.link} className="feature-card">
              <div className="feature-icon">{f.icon}</div>
              <div className="feature-content">
                <h3>{f.title}</h3>
                <p>{f.description}</p>
              </div>
              <ChevronRight className="feature-arrow" />
            </Link>
          ))}
        </div>

        {/* Activity */}
        <div className="activity-section">
          <div className="section-header">
            <h2>Recent Activity</h2>
            <Link to="/activity" className="link-primary">View All</Link>
          </div>
          <div className="activity-list">
            {/* same as your original */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
